import { useQuery, gql } from "@apollo/client";

const QUERY_ALL_USERS = gql`
  query getAllUsers {
    users {
      id
      name
      age
      nationality
    }
  }
`;

const DisplayUsers = () => {
  const { loading, error, data } = useQuery(QUERY_ALL_USERS);
  if (loading) {
    console.log("loading");
  } else if (data) {
    console.log(data);
  } else if (error) {
    console.log(error);
  }

  return <div>DisplayUsers</div>;
};

export default DisplayUsers;
